import { useState, useEffect } from "react"

const Home = () => {
    const [describtion, setDescribtion] = useState(null);
    const [script, setScript] = useState(null);
    const [file, setFile] = useState(null)

    useEffect(() => {
        fetchF();
      }, [script]);


      async function fetchF() {
        const myHeaders = new Headers();
    myHeaders.append("Content-Type", "application/json");

    const raw = JSON.stringify({
      script: script,
    });

    const requestOptions = {
      method: "POST",
      headers: myHeaders,
      body: raw,
      redirect: "follow",
    };

    await fetch("http://localhost:3000/api/create.php", requestOptions);

      }
async function handleClick(){
    if (!file) {
        alert('Please select a file.');
        return;
      }

      const formData = new FormData();
      formData.append('file', file);

    const response = await fetch('http://localhost/api/upload.php', {
        method: 'POST',
        body: formData,
      })
      const data = await response.json();
      setScript(data.text);
    }

    return(
        <>
        <input type="text" placeholder="type describtion" onChange={(e) => {setDescribtion(e.target.value)}}></input>
        <input type="file" placeholder="entrer the audio file" onChange={(e) => {setScript(e.target.value)}}></input>
        <button onClick={handleClick}>create</button>
        </>
    )
}
export default Home;
