import { useState } from "react"

const Home = () => {
    const [describtion, setDescribtion] = useState(null);
    const [script, setScript] = useState(null);
async function handleClick(){

    const myHeaders = new Headers();
    myHeaders.append("Content-Type", "application/json");

    const raw = JSON.stringify({
      'script': script,
      'describtion': describtion
    });

    const requestOptions = {
      method: "POST",
      headers: myHeaders,
      body: raw,
      redirect: "follow",
    };

    await fetch("http://localhost:3000/api/create.php", requestOptions);

}

    return(
        <>
        <input type="text" placeholder="type describtion" onChange={(e) => {setDescribtion(e.targe.value)}}></input>
        <input type="text" placeholder="entrer the audio file" onChange={(e) => {setScript(e.targe.value)}}></input>
        <button onClick={handleClick}>create</button>
        </>
    )
}
export default Home;
